import React,{ useState} from "react"
import './App.css';
import Post from "./Post";



function App() {
  const [posts, setposts] = useState([
    {
      username: "hrithik",
      imageUrl: "https://image.shutterstock.com/image-vector/set-short-pants-baby-boy-260nw-1337674454.jpg",
      caption: "wow it work fine"
    },
    {
      username: "some random person",
      imageUrl: "https://www.freecodecamp.org/news/content/images/size/w2000/2020/02/Ekran-Resmi-2019-11-18-18.08.13.png",
      caption: "ketty perry"     
    }
  ]) 
  return (
    <div className="app">
      <div className="app_header">
      <h1 className="title">Instagram</h1>
         {/* <i><FontAwesomeIcon icon={faSearch} ></FontAwesomeIcon></i> */}
      </div>
      {posts.map(post =>{
        return(
          <Post username={post.username} caption={post.caption} imageUrl={post.imageUrl}/>
        )
      })}
    </div>
  );
}

export default App;
