import React,{ useState, useEffect} from "react"
import './App.css';
import Post from "./Post";
import { db } from "./firebase";



function App() {
  const [posts, setposts] = useState([]) 

  // it run a piece of a code based on a specific condition
  useEffect(() => {
    //these is where the code run
    db.collection('instapost').onSnapshot(snapshot => {
      setposts(snapshot.docs.map(doc => doc.data()))
    })
  }, [posts])    //these is called conditon or if we leave these blank the code gonna run once then it will not run and if we fill with posts then code gonna run once and it goona run everytime posts chaange


  return (
    <div className="app">
      <div className="app_header">
      <h1 className="title">Instagram</h1>
         {/* <i><FontAwesomeIcon icon={faSearch} ></FontAwesomeIcon></i> */}
      </div>
      {posts.map(post =>{
        return(
          <Post username={post.username} caption={post.caption} imageUrl={post.imageUrl}/>
        )
      })}
    </div>
  );
}

export default App;
