import React, { useState , useEffect} from "react";
import './Post.css';
import Avatar from '@material-ui/core/Avatar';
import { Button } from "@material-ui/core";
import { db } from "./firebase";


function Post(props, {postId}) {
    const [comments, setComments] = useState([])
    const [comment, setComment] = useState("")        //to keep track of a single comment

    useEffect(() => {
        let unsubscribe;
        if(postId){
            unsubscribe=db.collection('instapost').doc(postId).collection("comments").onSnapshot((snapshot) => {
                setComments(snapshot.doc.map((doc) => doc.data()));

                });

        }

        return () => {
            unsubscribe();
        }
    },[postId])

    function postComment(event){

    }
    return (
        <div className="post">
        <div className="post_header">
            <Avatar className="post_avatar" alt="" src=""/>
            <h3>{props.username}</h3>
        </div>


            <img className="post_image" alt="" src={props.imageUrl}></img>
            <h4 className="post_text"><strong>{props.username}</strong> {props.caption}</h4>
            <form className="post_commentBox">
               <input className="post_input" type="text" placeholder="Add a comment..." value={comment} onChange={(e) => setComment(e.target.value)} />
               <button disabled={!comment} className="post_button" type="submit" onClick={postComment} />
            </form>

        </div>
    )
}

export default Post
